    $(function () {
        //获取短信验证码
        var validCode = true;
        $(".msg").click (function () {

            if ($("#myForm input[name='name']").val() == "" || $("#myForm input[name='tel']").val() == "") {
                alert("请完整填写信息！");
                return false;
            }
            else if (!$("#myForm input[name='tel']").val().match(/^1[34578]{1}\d{9}$/)) {
                alert("请正确填写手机号！");
                return false;
            }
            else{
                $.ajax({
                    cache: true,
                    type: "POST",
                    url: "<?php echo U('DuxCms/Form/miji_yz');?>",
                    data: $('#myForm').serialize(),// 你的formid
                    async: false,
                    error: function (request) {
                        alert("系统出错请重新获取");
                    },
                    success: function (data) {
//                    alert(data);
//                     $("#commonLayout_appcreshi").parent().html(data);

//                    alert("申请成功,立即下载!");
//                    setTimeout(window.location.href="/mianshimiji/fudan.pdf",3000);
                    }
                });

                var time = 60;
                var code = $(this);
                if (validCode) {
                    validCode = false;
                    code.addClass("msg");
                    var t = setInterval(function () {
                        time--;
                        code.html('还剩余(' + time + ')秒');
                        if (time == 0) {
                            clearInterval(t);
                            code.html("重新获取");
                            validCode = true;
                            code.removeClass("msg");

                        }
                    }, 1000)
                }
            }

        })
    });



</script>

